  agregarAlCarrito(producto: any) {
    const existente = this.carrito.find(p => p.nombre === producto.nombre);
    if (existente) {
      existente.cantidad += 1;
    } else {
      this.carrito.push({ ...producto, cantidad: 1 });
    }
  }

  eliminarDelCarrito(producto: any) {
    this.carrito = this.carrito.filter(p => p.nombre !== producto.nombre);
  }

  getTotalCarrito(): number {
    return this.carrito.reduce((total, item) => total + item.precio * item.cantidad, 0);
  }

  continuarConLaCompra() {
    
    this.route.navigate(['/pago']);
    localStorage.setItem('carrito', JSON.stringify(this.carrito));
    
  }

  productosPromocionOriginal = [...this.productosPromocion];  // copia original
lanzamientosRecientesOriginal = [...this.lanzamientosRecientes];
categoriasOriginal = [...this.categorias];

filtrarProductos(event: any) {
  const texto = event.target.value.toLowerCase();

  if (!texto) {
    // Si el campo está vacío, restauramos todo
    this.productosPromocion = [...this.productosPromocionOriginal];
    this.lanzamientosRecientes = [...this.lanzamientosRecientesOriginal];
    this.categorias = [...this.categoriasOriginal];
    return;
  }

  // Filtrar productos en promoción
  this.productosPromocion = this.productosPromocionOriginal.filter(producto =>
    producto.nombre.toLowerCase().includes(texto) ||
    producto.marca.toLowerCase().includes(texto)
  );

  // Filtrar lanzamientos recientes
  this.lanzamientosRecientes = this.lanzamientosRecientesOriginal.filter(producto =>
    producto.nombre.toLowerCase().includes(texto) ||
    producto.marca.toLowerCase().includes(texto)
  );

  // Filtrar productos por categoría
  this.categorias = this.categoriasOriginal.map(categoria => {
    return {
      ...categoria,
      productos: categoria.productos.filter(producto =>
        producto.nombre.toLowerCase().includes(texto) ||
        producto.marca.toLowerCase().includes(texto)
      )
    };
  });
}


